";
    }

    @keyframes spin {
      to {
        --bg-angle: 360deg;
      }
    }

    article {
      animation: spin 2.5s infinite linear paused;
      background:
        linear-gradient(
            to bottom,
            oklch(0.35 0.37 330 / 0.95),
            oklch(0.35 0.37 330 / 0.95)
          )
          padding-box,
        conic-gradient(
            from var(--bg-angle),
            oklch(0.95 0.17 0.0) 0 0
          )
          border-box;
      border: 6px solid transparent;
      border-radius: 1rem;
      box-shadow: 0.125rem 0.25rem 0.25rem 0.5rem oklch(0.1 0.37 315 / 0.25);
      color: white;
      padding: 1rem;
      width: min(400px, 90vw);
    }

    article:hover {
      animation-play-state: running;
    }

    * {
      box-sizing: border-box;
    }

    html {
      font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
      font-size: 18px;
      line-height: 1.45;
    }

    body {
      margin: 0;
      height: 100%;
      width: 100%;
      display: flex;
      justify-content: center;
      align-items: center;
      background:
        radial-gradient(
            circle,
            oklch(0.15 0.2 330 / 0),
            oklch(0.15 0.2 330 / 1)
          ),
        linear-gradient(
            344deg,
            oklch(0.3 0.37 310),
            oklch(0.35 0.37 330),
            oklch(0.3 0.37 310)
          );
    }
